import store from "../../firebase/firebase.js";



  const Noticias =() => {

    const noticia= store.collection("noticia").get().then((snapshot)=>{
      snapshot.docs.forEac
    })

    console.log(JSON.stringify(noticia,null,2))




  return ( 
    <section className='info-last'>
      <div className='contenedor last-section'>
        <div className='contenedor-textos-main'>
          <h2 className='titulo left'>{noticia.titulo}</h2>
          <p className='parrafo'>{noticia.contenido}</p>
        </div>
        <div className='fmt-fecha'>
          <div>{noticia.autor}</div>
          <div>{noticia.fecha}</div>
        </div>
        <img src={noticia.image} alt='noticia' />
      </div>
    </section>
    )
  };



export default Noticias;
