import { useRef } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import store from "../../firebase/firebase.js";



const CrearNoticia = () => {
  
  const fecha = new Date().toLocaleDateString();
  const fechaRef = useRef();
  const tituloRef = useRef();
  const contenidoRef = useRef();
  const autorRef = useRef();
  const imagenRef = useRef();

  const handlerClick = () => {

    const publicacion =
      {
       
        titulo: tituloRef.current.value,
        fecha: fechaRef.current.defaultValue,
        contenido: contenidoRef.current.value,
        autor: autorRef.current.value,
        image: imagenRef.current.value,
      }
      
      
      

      store.collection("cursos").doc("p3TjDoNXKrBGnvgebuFi").set({
        area: areaRef.current.value,
        curso: cursoRef.current.defaultValue,
        costo: costoRef.current.value,
        modalidad: modalidadRef.current.value,
        
    })
    .then(() => {
        alert("Noticia actualizada con exito!");
    })
    .catch((error) => {
        alert("Error alactualizar: ", error);
    });
     
    
      
      areaRef.current.value = "";
      cursoRef.current.value = "";
      costoRef.current.value = "";
      
};
  return (
    <section className='info-last' >
      <div className='contenedor' >
        <div>
          <h2>pruebas cargas de cursos</h2>
        </div>
        <div style={{ paddingLeft: "30px", paddingRight: "30px" }}>
          <Form>
            
            <Form.Group className='mb-3' controlId='formGroupTitulo'>
              <Form.Label style={{ color: "#000000" }}>Area</Form.Label>
              <Form.Control type='text' placeholder='Ingrese el area del curso' ref={areaRef} style={{ width: "100%" }} />
            </Form.Group>
            <Form.Group className='mb-3' controlId='formGroupContenido'>
              <Form.Label style={{ color: "#000000" }}>curso </Form.Label>
              <Form.Control as='textarea' style={{ height: "200px" }} placeholder='Nombre del curso' ref={cursoRef} />
            </Form.Group>
            <Form.Group className='mb-3' controlId='formGroupAutor'>
              <Form.Label style={{ color: "#000000" }}>costo</Form.Label>
              <Form.Control type='text' placeholder='Ingrese el valor del curso' ref={costoRef} style={{ width: "100%" }} />
            </Form.Group>
            <Form.Group className='mb-3' controlId='formGroupAutor'>
              <Form.Label style={{ color: "#000000" }}>Modalidad</Form.Label>
              <Form ref={modalidadRef}>
                {['checkbox', 'radio'].map((type) => (
                    <div key={`inline-${type}`} className="mb-3">
                    <Form.Check
                        inline
                        label="presencial"
                        name="group1"
                        type={type}
                        id={`inline-${type}-1`}
                    />
                    <Form.Check
                        inline
                        label="virtual"
                        name="group1"
                        type={type}
                        id={`inline-${type}-2`}
                    />
                    </div>
                ))}
                </Form>
            </Form.Group>
          </Form>
          <Button variant='primary' onClick={() => handlerClick()}>
            cargar curso
          </Button>
        </div>
      </div>
    </section>
  );
};
export default CargaCurso;
