import store from "../../firebase/firebase.js";

const Noticias = () => {


  var noticia = store.collection("noticia").doc("S0jbsa0SxZ5DIGwBMd36p");

  noticia.get().then((doc) => {
    if (doc.exists) {
        console.log("Document data:", doc.data());
    } else {
        // doc.data() will be undefined in this case
        console.log("No such document!");
    }
}).catch((error) => {
    console.log("Error getting document:", error);
});


    return ( 
    <section className='info-last'>
      <div className='contenedor last-section'>
        <div className='contenedor-textos-main'>
          <h2 className='titulo left'>{noticia.titulo}</h2>
          <p className='parrafo'>{noticia.titulo}</p>
        </div>
        <div className='fmt-fecha'>
          <div>{noticia.autor}</div>
          <div>{noticia.fecha}</div>
        </div>
        <img src={noticia.image} alt='noticia' />
      </div>
    </section>
    )

};
export default Noticias;
