import React from "react";
import {store} from "../../firebase/firebase.js";

const Noticias = () => {

  const noticia = store.collection("noticia").get()
  console.log("noticia", noticia)

  return noticia.map((n) => {
    return ( 
    <section className='info-last'>
      <div className='contenedor last-section'>
        <div className='contenedor-textos-main'>
          <h2 className='titulo left'>{n.titulo}</h2>
          <p className='parrafo'>{n.contenido}</p>
        </div>
        <div className='fmt-fecha'>
          <div>{n.autor}</div>
          <div>{n.fecha}</div>
        </div>
        <img src={n.image} alt='noticia' />
      </div>
    </section>
    )
  });
};
export default Noticias;
